import React, { useState } from 'react';

const DeleteCartComponent = () => {
  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const handleDelete = () => {
    // Implement your delete logic here
    console.log('Cart deleted');
    handleClose();
  };

  return (
    <div className="p-3">
      <div typeof='button' className="btn-danger d-flex align-items-center" onClick={handleShow}>
        <div size={24} className="me-2" />
        Delete Cart
      </div>

      <div className='modal' >
        <div className='modal-header' closeButton>
          <div className='modal-title'>Confirm Deletion</div>
        </div>
        <div className='modal-body'>
          Are you sure you want to delete your cart? This action cannot be undone. This will permanently delete the cart and remove the data from our servers.
        </div>
        <div className='modal-footer'>
          <div className="btn-secondary" onClick={handleClose}>
            Cancel
          </div>
          <div className='button btn-danger' onClick={handleDelete}>
            Delete
          </div>
        </div>
      </div>
    </div>
  );
};

export default DeleteCartComponent;
