import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { API_URLS } from '../config/apiConfig';

function ProductsForCart( orders ) {
  const [stocks, setStocks] = useState([]);
  const { accessToken } = useAuth();
  const { limit } = useState(100);
  const { skip } = useState(0);
  const [existingOrdersId, setExisting] = useState({ id:''});

  useEffect(() => {
    if (accessToken) {
      Object.values(orders).forEach(order =>{
        setExisting(order.product.id);
      });
      fetch(API_URLS.STOCKS/`?skip=${skip}&limit=${limit}`, {
        headers: {
          'Authorization': `Bearer ${accessToken}`
        }
      })
        .then(response => response.json())
        .then(data => setStocks(data['products']))
        .catch(error => console.error('Error fetching stocks:', error));
    }
  }, [accessToken]);

  return (
    <div className="container mt-4">
      <div className="row">
        <div className="col-md-8">
          <p>Available Products</p>
        </div>
        <div className="col-md-8">
          <ul className="list-group">
          {/* <li className="list-group-item d-flex justify-content-between align-items-center my-2">
              <h2 className='col-4'> Name </h2>
              <div className='display-6 col-4'> Quantity </div>
              <div className='display-6 col-4'>Action</div>
            </li> */}
            {stocks.map(stock => {
            if (!existingOrdersId.includes(stock.id)){
              <li key={stock.id} className="list-group-item d-flex justify-content-between align-items-center my-2">
              <h2 className='col-4'> {stock.title} </h2> 
              <div className='display-6'> {stock.units} </div>
                <Link to={`/stock-edit/${stock.id}`} className="btn btn-primary btn-sm col-4">Edit</Link>
              </li>
            }})}
          </ul>
        <Link to={`/add-product`} className=' btn btn-primary '>Add New Products</Link>
        </div>
      </div>
    </div>
  );
}

export default ProductsForCart;
