import React, { useState } from 'react';
import { Modal, Button } from 'react-bootstrap';

const DeleteCartComponent = () => {
  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const handleDelete = () => {
    // Implement your delete logic here
    console.log('Cart deleted');
    handleClose();
  };

  return (
    <div className="p-3">
      <Button variant="danger" onClick={handleShow} className="d-flex align-items-center">
        <Trash2 size={24} className="me-2" />
        Delete Cart
      </Button>

      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Confirm Deletion</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          Are you sure you want to delete your cart? This action cannot be undone. This will permanently delete the cart and remove the data from our servers.
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Cancel
          </Button>
          <Button variant="danger" onClick={handleDelete}>
            Delete
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default DeleteCartComponent;
