import React, { useState, useEffect } from 'react';
import { Link, useParams } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { API_URLS } from '../config/apiConfig';

function Carts() {
  const { uncleared } = useParams();
  const [carts, setCarts] = useState([]);
  const { accessToken } = useAuth();

  useEffect(() => {
    if (accessToken) {
      fetch(`${API_URLS.GET_CARTS}/?uncleared_only=true`, {
        headers: {
          'Authorization': `Bearer ${accessToken}`
        }
      })
        .then(response => response.json())
        .then(data => setCarts(data['carts']))
        .catch(error => console.error('Error fetching stocks:', error));
    }
  }, [accessToken]);

  return (
    <div className="container mt-4">
      <h2 className='text-center'>Carts</h2>
      <div className="row">
        <div className="container-fluid col-md-12 my-4">
          <ul className="list-group">
            {carts.map(cart => (
              <li key={cart.id} className="list-group-item d-flex flex-column my-3">
             <div className='row d-flex my-2 mx-2 display-6'>
                <div className='col-12'>{cart.id} </div>
              </div>
              <div className='row d-flex justify-content-between'>
                <div className='display-6'> Total_Amount: #{cart.total_amount>0 ? cart.total_amount : 0} </div>
                <div className='h3'>Status: {`${cart.status === false ? 'Cleared' : 'Pending'}`}</div>
                <button className={`btn ${cart.status === false ? 'btn-primary' : 'btn-secondary disabled'}`}
                  disabled={cart.status !== false } >
                  {cart.status === false ? (
                      <Link to={`/cart-edit/${cart.id}`} className="btn btn-primary btn-sm">Update</Link>
                  ) : (
                      <span>Cleared</span>
                  )}
                  </button>                
                </div>
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
}

export default Carts;
