import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { API_URLS } from '../config/apiConfig';
import CartItems from './CartItemsList';
import ProductsForCart from './GetProductsForCart';

function CartEdit() {
  const { id } = useParams();
  const navigate = useNavigate();
  const { accessToken } = useAuth();
  const [items, setItems] = useState([]);
  const [cart_total, setTotal] = useState(0);
  const [cart_status, setCartStatus] = useState();

  useEffect(() => {
    if (accessToken) {
      fetch(`${API_URLS.GET_CART}/?cart_id=${id}`, {
        headers: {
          'Authorization': `Bearer ${accessToken}`
        }
      })
        .then(response => response.json())
        .then(data => {
            setTotal(data['total_amount']);
            setItems(data['orders']);
            setCartStatus(data['status']);
            console.log(cart_status);
        })
        .catch(error => console.error('Error fetching stock:', error));
    }
  }, [accessToken, id]);

  return (
    <div className='row'>
        <div className="container mt-4 col-5">
        <h2>Cart Detail: {id}</h2>
            <div>
                <CartItems items={ items } />
            </div>
            <button className='btn btn-danger'>
            {`${cart_status === false ?<Link to={`/delete-cart/${id}`} />
               : <span></span>}`}
            </button>
            <button type="submit" className="btn btn-primary">Update Stock</button>
            <button type="button" className="btn btn-secondary ms-2" onClick={() => navigate('/carts')}>Cancel</button>
        </div>
        <div className='container ml-2 col-4'>
            <h2>Cart Summary</h2>
            <p>Total Amount: #{ cart_total }</p>
            <div className='container-fluid my-4'>
                <ProductsForCart orders={ items } />
            </div>
        </div>
    </div>
  );
}

export default CartEdit;
