import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import { API_URLS } from '../config/apiConfig';
import InventoryStats from './InventoryStats';
import StockList from './StockList';
import RecentOrders from './RecentOrders';
import { Link } from 'react-router-dom';

function HomePage() {
  const [stocks, setStocks] = useState([]);
  const [orders, setOrders] = useState([]);
  const [stats, setStats] = useState({ totalItems: 0, finishedStocks: 0 });
  const { accessToken } = useAuth();

  useEffect(() => {
    if (accessToken) {
      // Fetch stocks
      fetch(API_URLS.STOCKS, {
        headers: {
          'Authorization': `Bearer ${accessToken}`
        }
      })
        .then(response => response.json())
        .then(data => {
          data = data['products'];
          setStocks(data);
          const totalItems = data.reduce((sum, item) => sum + item.units, 0);
          const finishedStocks = data.filter(item => item.units === 0).length;
          setStats({ totalItems, finishedStocks });
        });

      // Fetch orders
      fetch(API_URLS.RECENT_ORDERS, {
        headers: {
          'Authorization': `Bearer ${accessToken}`
        }
      })
        .then(response => response.json())
        .then(data => {
            data = data['carts'];
            setOrders(data);
        }
        );
    }
  }, [accessToken]);

  if (!accessToken) {
    return <div className="container mt-4">Please sign in to view the dashboard.</div>;
  }

  return (
    <div className="container mt-4">
      <h1>DominionInventory Dashboard</h1>
      <InventoryStats stats={stats} />
      <div className='d-flex justify-content-center'>
        <Link to={'/cart-create'} className='btn btn-primary my-2 text-center col-8'>Create New Order</Link>
      </div>
      <div className="d-flex mt-4 justify-content-between">
        <div className="col-md-5">
          <StockList stocks={stocks} />
        </div>
        <div className="col-md-5">
          <RecentOrders orders={orders} />
        </div>
      </div>
    </div>
  );
}

export default HomePage;
