import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { API_URLS } from '../config/apiConfig';

function ProductsForCart( orders ) {
  const [stocks, setStocks] = useState([]);
  const { accessToken } = useAuth();
  const [limit, setLimit] = useState(100);
  const [skip, setSkip] = useState(0);
  const [existingOrdersId, setExisting] = useState([]);

  useEffect(() => {
    if (accessToken) {
        if(Array.isArray(orders) && orders.length>0){
        orders.map(order => {
            console.log(order);
            setExisting(prevExisting => [...prevExisting, order]);
          });}

      setSkip(0);
      setLimit(100);

      fetch(`${API_URLS.STOCKS}/?skip=${skip}&limit=${limit}`, {
        headers: {
          'Authorization': `Bearer ${accessToken}`
        }
      })
        .then(response => response.json())
        .then(data => setStocks(data['products']))
        .catch(error => console.error('Error fetching stocks:', error));
    }
  }, [accessToken, orders]);

  return (
    <div className="container mt-4">
      <div className="row">
        <div className="col-md-8">
          <p>Available Products</p>
        </div>
        <div className="col-md-8">
          <ul className="list-group">

            {stocks.map(stock => {

              <li key={stock.id} className="list-group-item d-flex justify-content-between align-items-center my-2">
              <h2 className='col-4'> {stock.title} </h2> 
              <div className='display-6'> {stock.units} </div>
                <Link to={`/stock-edit/${stock.id}`} className="btn btn-primary btn-sm col-4">Edit</Link>
              </li>
            })}
          </ul>
        <Link to={`/add-product`} className=' btn btn-primary '>Add New Products</Link>
        </div>
      </div>
    </div>
  );
}

export default ProductsForCart;
