import React from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import { AuthProvider, useAuth } from './context/AuthContext';
import HomePage from './components/HomePage';
import Carts from './components/Carts';
import Products from './components/Products';
import SignIn from './components/SignIn';
import StockEdit from './components/StockEdit';
import CartEdit from './components/CartEdit';
import AddProductForm from './components/AddProduct';

function NavBar() {
  const { accessToken, setAccessToken } = useAuth();

  const handleSignOut = () => {
    setAccessToken(null);
  };

  return (
    <nav className="navbar navbar-expand-lg navbar-light bg-light">
      <div className="container">
        <Link className="navbar-brand" to="/">DominionInventory</Link>
        <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
          <span className="navbar-toggler-icon"></span>
        </button>
        <div className="collapse navbar-collapse" id="navbarNav">
          <ul className="navbar-nav">
            <li className="nav-item">
              <Link className="nav-link" to="/carts">Carts</Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link" to="/products">Products</Link>
            </li>
            {accessToken ? (
              <li className="nav-item">
                <button className="nav-link btn btn-link" onClick={handleSignOut}>Sign Out</button>
              </li>
            ) : (
              <li className="nav-item">
                <Link className="nav-link" to="/signin">Sign In</Link>
              </li>
            )}
          </ul>
        </div>
      </div>
    </nav>
  );
}


function App() {
  return (
    <AuthProvider>
      <Router>
        <div className="App">
          <NavBar />
          <Routes>
            <Route path="/" element={<HomePage />} />
            <Route path="/carts" element={<Carts />} />
            <Route path="/products" element={<Products />} />
            <Route path="/signin" element={<SignIn />} />
            <Route path="/stock-edit/:id" element={<StockEdit />} />
            <Route path="/cart-edit/:id" element={<CartEdit />} />
            <Route path="/add-product" element={<AddProductForm />} />
          </Routes>
        </div>
      </Router>
    </AuthProvider>
  );
}

export default App;
