import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { API_URLS } from '../config/apiConfig';

function Carts() {
  const [stocks, setCarts] = useState([]);
  const { accessToken } = useAuth();

  useEffect(() => {
    if (accessToken) {
      fetch(API_URLS.GET_CARTS, {
        headers: {
          'Authorization': `Bearer ${accessToken}`
        }
      })
        .then(response => response.json())
        .then(data => setStocks(data['products']))
        .catch(error => console.error('Error fetching stocks:', error));
    }
  }, [accessToken]);

  return (
    <div className="container mt-4">
      <h2>Products</h2>
      <div className="row">
        <div className="col-md-8">
          <p>Product information and management tools can go here.</p>
        </div>
        <div className="col-md-8">
          <h3>Inventory Stocks</h3>
          <ul className="list-group">
            {stocks.map(stock => (
              <li key={stock.id} className="list-group-item d-flex justify-content-between align-items-center">
              <h2 className='col-4'> {stock.title} </h2> 
              <div className='display-6'> {stock.units} </div>
                <Link to={`/stock-edit/${stock.id}`} className="btn btn-primary btn-sm">Edit</Link>
              </li>
            ))}
          </ul>
        <Link to={`/add-product`} className=' btn btn-primary '>Add New Products</Link>
        </div>
      </div>
    </div>
  );
}

export default Products;
