import React, { useState, useEffect, useRef } from 'react';

const AddStockModal = ({ isOpen, onClose, onAddStock, availableProducts}) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [sortOrder, setSortOrder] = useState('asc');
  const [filteredProducts, setFilteredProducts] = useState([]);
  const modalRef = useRef(null);
  const searchInputRef = useRef(null);

  useEffect(() => {
    if(!Array.isArray(availableProducts))
        return;
    const filtered = availableProducts.filter(product => 
      product.title.toLowerCase().includes(searchTerm.toLowerCase())
    );
    const sorted = filtered.sort((a, b) => {
      if (sortOrder === 'asc') {
        return a.title.localeCompare(b.name);
      } else {
        return b.title.localeCompare(a.name);
      }
    });

    setFilteredProducts(sorted);
  }, [searchTerm, sortOrder, availableProducts]);

  useEffect(() => {
    if (isOpen) {
      document.body.style.overflow = 'hidden';
      searchInputRef.current?.focus();
    } else {
      document.body.style.overflow = 'unset';
    }

    return () => {
      document.body.style.overflow = 'unset';
    };
  }, [isOpen]);

  useEffect(() => {
    const handleEscape = (event) => {
      if (event.key === 'Escape') {
        onClose();
      }
    };

    if (isOpen) {
      document.addEventListener('keydown', handleEscape);
    }

    return () => {
      document.removeEventListener('keydown', handleEscape);
    };
  }, [isOpen, onClose]);

  if (!isOpen) return null;

  return (
    <div 
      className="modal d-block" 
      tabIndex="-1" 
      role="dialog"
      onClick={onClose}
    >
        <div className="modal-content" ref={modalRef}>
          <div className="modal-header">
            <h5 className="modal-title">Add New Stock</h5>
            <button type="button" className="btn-close" onClick={onClose} aria-label="Close"></button>
          </div>
          <div className="modal-body">
            <div className="mb-3">
              <input 
                ref={searchInputRef}
                type="text" 
                className="form-control" 
                placeholder="Search products..." 
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
            </div>
            <div className="mb-3">
              <select 
                className="form-select" 
                value={sortOrder} 
                onChange={(e) => setSortOrder(e.target.value)}
              >
                <option value="asc">Sort A-Z</option>
                <option value="desc">Sort Z-A</option>
              </select>
            </div>
            <ul className="list-group">
              {filteredProducts.map(product => (
                <li key={product.id} className="list-group-item d-flex justify-content-between align-items-center">
                  {product.title}
                  <button 
                    className="btn btn-primary btn-sm" 
                    onClick={() => onAddStock(product)}
                  >
                    Add
                  </button>
                </li>
              ))}
            </ul>
          </div>
          <div className="modal-footer">
            <button type="button" className="btn btn-secondary" onClick={onClose}>Close</button>
          </div>
        </div>
      <div className="modal-backdrop fade show"></div>
    </div>
  );
};

export default AddStockModal;
